local function SafeExecute(url, vars)
    if vars then
        for key, value in pairs(vars) do
            if getgenv then 
                getgenv()[key] = value 
            end
            if _G then
                _G[key] = value
            end
        end
    end

    local success, content = pcall(function()
        return game:HttpGet(url)
    end)

    if not success then
        warn("HTTP请求失败，请检查网络或链接是否有效")
        return
    end

    if string.find(content, "404: Not Found") or string.len(content) < 5 then
        warn("错误: 脚本链接失效 (404) 或内容为空")
        return
    end

    local loadSuccess, loadResult = pcall(function()
        return loadstring(content)
    end)

    if not loadSuccess then
        warn("Loadstring 编译失败 (可能是脚本语法错误): " .. tostring(loadResult))
        return
    end

    local runSuccess, runErr = pcall(function()
        loadResult()
    end)
    
    if not runSuccess then
        warn("脚本运行时出错: " .. tostring(runErr))
    else
        print("脚本执行成功!")
    end
end

local function CheckGroup()
    local success, result = pcall(function()
        return game:GetService("Players").LocalPlayer:IsInGroup(Config.GroupID)
    end)
    if not success or not result then
        if setclipboard then setclipboard(Config.GroupInfo) end
        game.Players.LocalPlayer:Kick(Config.KickMessage)
        return false
    end
    return true
end

if not CheckGroup() then return end

local placeId = game.PlaceId
local MarketplaceService = game:GetService("MarketplaceService")

for _, data in ipairs(ScriptDatabase) do
    if data.ids then
        for _, id in ipairs(data.ids) do
            if id == placeId then
                warn("匹配到游戏id: " .. data.name)
                SafeExecute(data.url, data.vars)
                return
            end
        end
    end
end

local success, info = pcall(function()
    return MarketplaceService:GetProductInfo(placeId)
end)

if success and info and info.Creator then
    local creatorId = info.Creator.CreatorTargetId
    for _, data in ipairs(ScriptDatabase) do
        if data.creatorIds then
            for _, id in ipairs(data.creatorIds) do
                if id == creatorId then
                    warn("匹配到作者id: " .. data.name)
                    SafeExecute(data.url, data.vars)
                    return
                end
            end
        end
    end
end

warn("未匹配到任何脚本url支持此游戏")